import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './products.css';

export default function Tshirt() {
  const [tshirts, setTshirts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/products/tshirts')
      .then(response => response.json())
      .then(data => setTshirts(data))
      .catch(err => console.error('Error fetching t-shirts:', err));
  }, []);

  const handleClick = (product) => {
    navigate('/ProductDetail', { state: { product } });
  };

  return (
    <div className='tshirts'>
      <section className="hero tshirts-hero-banner" />

      {/* title */}
      <div className='title'>
        <h1>T-Shirts</h1>
        <div className="vector-line"></div>
      </div>

      {/* products */}
      <div className="product-row">
        {tshirts.map(product => (
          <div
            key={product.productId}
            className="product-frame"
            onClick={() => handleClick(product)}
            style={{ cursor: 'pointer' }}
          >
            <img
              src={product.imageurl}
              alt={product.name}
              className="product-image"
            />
            <div className="product-title">{product.name}</div>
            <div className="product-price">
              ${Array.isArray(product.price) ? product.price[0].toFixed(2) : product.price}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
