import React, { useState } from 'react';
import './ProductDetail.css';

export default function ProductDetail() {
  const [quantity, setQuantity] = useState(1);
  const [selectedSize, setSelectedSize] = useState(null);

  const increaseQuantity = () => setQuantity(q => q + 1);
  const decreaseQuantity = () => setQuantity(q => (q > 1 ? q - 1 : 1));

  const sizes = ['50”', '52”', '54”', '56”'];

  const handleAddToCart = () => {
    alert(`Added ${quantity} of size ${selectedSize || 'none'} to cart.`);
  };

  return (
    <div className="product-container">
      {/* Background image */}
      <img
        src="/productdetailbackground.svg"
        alt="Decorative Background"
        className="background-image"
      />

      {/* Content wrapper */}
      <div className="product-content">
        <h1 className="product-detail-title">4'7 Blue Inferno</h1>
        <hr className="divider" />
        <p className="product-detail-price">$275.00</p>

        <div className="quantity-selector">
          <button onClick={decreaseQuantity}>-</button>
          <span>{quantity}</span>
          <button onClick={increaseQuantity}>+</button>
        </div>

        <button className="add-to-cart" onClick={handleAddToCart}>
          Add To Cart
        </button>

        <div className="size-selector">
          {sizes.map((size) => (
            <button
              key={size}
              className={`sizingbuttonContainer ${
                selectedSize === size ? 'selectedButton' : ''
              }`}
              onClick={() => setSelectedSize(size)}
            >
              <div className="rectangle2" />
              <span className="myVar">{size}</span>
            </button>
          ))}
        </div>

        <div className="product-description">
          The Blue Inferno is crafted for riders who live for that perfect rush between sand and sea.<br /><br />
          Its clean white base keeps it classic and sharp, while bold blue flames and coral designs lick the edges, giving it an untamed, oceanic energy.<br /><br />
          Lightweight but sturdy, this board is designed for quick cuts, smooth glides, and powerful wraps.<br /><br />
          Whether you're just skimming the surface or carving deep lines, the Blue Inferno gives you the perfect balance of speed, stability, and style.<br /><br />
          Built for all levels, it's the ultimate board for chasing adrenaline and mastering the shoreline.
        </div>

        <div className="features">
          <h2>Build Tech</h2>
          <ul>
            <li>3/4” Thick</li>
            <li>V.R.T.™ Technology</li>
            <li>Continuous Core</li>
            <li>E-Glass™ Wrap</li>
            <li>PolyLam™ Texture Finish</li>
            <li>FlexSpine™ Carbon Stringer</li>
            <li>Resin Art</li>
            <li>Polyester Resin</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
